// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.5.0;

contract Marketplace {
    string public name;
    uint public productCount = 0;
    mapping(uint => Product) public products; //mapping includes a unique id. Here we select the uint id.

    struct Product {
        uint id;
        string name;
        uint price;
        address payable owner;
        bool purchased;
    }

    event productCreated(
        uint id,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

    event ProductPurchased(
        uint id,
        string name,
        uint price,
        address payable owner,
        bool purchased
    );

    constructor() public {
        name = "Marketplace";
    }

    function createProduct(string memory _name, uint _price) public {
        //Require a valid name
        require(bytes(_name).length > 0);
        //Require a valid price
        require(_price > 0);
        //Increment Product Count
        productCount++;
        //Create the product
        products[productCount] = Product(productCount, _name, _price, address(uint160(msg.sender)), false);
        //Trigger an event
        emit productCreated(productCount, _name, _price, address(uint160(msg.sender)), false);
    }

    function purchaseProduct(uint _id) public payable {
        //Fetch the Product
        Product memory _product = products[_id];

        //Fetch the owner
        address payable _seller = _product.owner;

        //Make sure the product has a valid id
        require(_product.id > 0 && _product.id <= productCount);

        //Require that there is enough Ether in the transaction
        require(msg.value >= _product.price);

        //Require that the product has not been purchased already
        require(!_product.purchased);

        //Require that the buyer is not the seller
        require(_seller != msg.sender);

        //Transfer ownership to the buyer
        _product.owner = address(uint160(msg.sender));

        //Marked as purchased
        _product.purchased = true;

        //Update the product
        products[_id] = _product;

        //Pay the seller by sending them Ether
        _seller.transfer(msg.value);

        //Trigger an event
        emit ProductPurchased(productCount, _product.name, _product.price, address(uint160(msg.sender)), true);
    }
}
